return ['po-revision-date'=>'2024-05-25 13:36:40+0000','mime-version'=>'1.0','content-type'=>'text/plain; charset=UTF-8','content-transfer-encoding'=>'8bit','plural-forms'=>'nplurals=2; plural=n != 1;','x-generator'=>'GlotPress/4.0.1','language'=>'et_EE','project-id-version'=>'WordPress - 6.5.x - Development - Administration - Network Admin','messages'=>['%1$s &#8211; %2$s'=>'%1$s &#8211; %2$s','%s KB'=>'%s KB','%s Sites'=>'%s saidid','%s theme deleted.'=>'%s teema kustutati.' . "\0" . '%s teemat kustutati.','%s theme disabled.'=>'%s teema välja lülitatud.' . "\0" . '%s teemat välja lülitatud.','%s theme enabled.'=>'%s teema sisse lülitatud.' . "\0" . '%s teemat sisse lülitatud.','%s theme will be auto-updated.'=>'%s teemat uuendatakse edaspidi automaatselt.' . "\0" . '%s teemat uuendatakse edaspidi automaatselt.','%s theme will no longer be auto-updated.'=>'%s teemat ei uuendata enam automaatselt.' . "\0" . '%s teemat ei uuendata enam automaatselt.','<a href="https://codex.wordpress.org/Network_Admin_Themes_Screen">Documentation on Network Themes</a>'=>'<a href="https://codex.wordpress.org/Network_Admin_Themes_Screen" target="_blank">Võrgustiku teemade dokumentatsioon</a>','<a href="https://codex.wordpress.org/Network_Admin_Users_Screen">Documentation on Network Users</a>'=>'<a href="https://codex.wordpress.org/Network_Admin_Users_Screen" target="_blank">Võrgustiku kasutajate dokumentatsioon</a>','<a href="https://wordpress.org/documentation/article/create-a-network/">Documentation on Creating a Network</a>'=>'<a href="https://wordpress.org/documentation/article/create-a-network/">Dokumentatsioon võrgustiku loomise kohta</a>','<a href="https://wordpress.org/documentation/article/network-admin-settings-screen/">Documentation on Network Settings</a>'=>'<a href="https://wordpress.org/documentation/article/network-admin-settings-screen/">Dokumentatsioon võrgustiku sätete kohta</a>','<a href="https://wordpress.org/documentation/article/network-admin-updates-screen/">Documentation on Upgrade Network</a>'=>'<a href="https://wordpress.org/documentation/article/network-admin-updates-screen/">Dokumentatsioon võrgustiku uuendamise kohta</a>','<a href="https://wordpress.org/documentation/article/network-admin/">Documentation on the Network Admin</a>'=>'<a href="https://wordpress.org/documentation/article/network-admin/">Dokumentatsioon võrgustikuhalduri kohta</a>','<a href="https://wordpress.org/documentation/article/tools-network-screen/">Documentation on the Network Screen</a>'=>'<a href="https://wordpress.org/documentation/article/tools-network-screen/">Dokumentatsioon võrgustikuekraani kohta</a>','A new user will be created if the above email address is not in the database.'=>'Kui seda aadressi andmebaasis ei ole, luuakse uus kasutaja.','A password reset link will be sent to the user via email.'=>'Paroolivahetuse link saadetakse kasutajale e-postiga.','Active Child Theme'=>'Aktiivne alamteema','Add New Users'=>'Lisa kasutajaid','Add New takes you to the Add New Site screen. You can search for a site by Name, ID number, or IP address. Screen Options allows you to choose how many sites to display on one page.'=>'Lisa uus viib su uue saidi lisamise lehele. Sa saad saiti otsida nime, tunnuse või IP-aadressi alusel. Ekraani seaded lubab muuta korraga kuvatavate saitide arvu.','Add Site'=>'Lisa sait','Add User'=>'Lisa kasutaja','Add User will set up a new user account on the network and send that person an email with username and password.'=>'Lisa kasutaja loob uue võrgustikus uue kasutajakonto ja saadab sellele isikule eposti tema kasutajanime ja parooliga.','Add and manage sites or users'=>'Lisa ja halda saite või kasutajaid','Add the designated lines of code to wp-config.php (just before <code>/*...stop editing...*/</code>) and <code>.htaccess</code> (replacing the existing WordPress rules).'=>'Lisa näidatud koodiread wp-config.php faili (täspelt enne <code>/*...stop editing...*/</code>) ja <code>.htaccess</code> faili (asendades olemasolevad WordPressi reeglid).','Add the following to your %1$s file in %2$s <strong>above</strong> the line reading %3$s:'=>'Lisa järgnev kood kataloogis %2$s olevasse faili %1$s <strong>enne</strong> rida tekstiga %3$s:','Add the following to your %1$s file in %2$s, <strong>replacing</strong> other WordPress rules:'=>'Lisa järgnev %1$s faili kataloogis %2$s (teised WordPressi reeglid <strong>asendatakse</strong>):','Addresses of Sites in your Network'=>'Võrgustiku saitide aadressid','Admin Email'=>'Halduri e-aadress','All Sites'=>'Kõik saidid','All done!'=>'Kõik on valmis!','Allow new registrations'=>'Kasutajate registreerumine on lubatud','Allow site administrators to add new users to their site via the "Users &rarr; Add New" page'=>'Luba saidi halduritel uute kasutajate lisamine "Kasutajad &rarr; Lisa uus" kaudu','Allowed file types. Separate types by spaces.'=>'Lubatud falitüübid. Eralda tüübid tühikutega.','An Edit link to a separate Edit Site screen.'=>'Muuda link eraldiseisvale Muuda saiti lehele.','An existing WordPress network was detected.'=>'Leiti olemasolev WordPressi võrgustik.','Archived'=>'Arhiveeritud','Archived <span class="count">(%s)</span>'=>'Arhiveeritud <span class="count">(%s)</span>' . "\0" . 'Arhiveeritud <span class="count">(%s)</span>','Are you sure you want to delete these themes?'=>'Oled kindel, et soovid need teemad kustutada?','Attributes'=>'Atribuudid','Banned Email Domains'=>'Keelatud e-postidomeenid','Banned Names'=>'Keelatud nimed','Because you are using %1$s, the sites in your WordPress network must use sub-directories. Consider using %2$s if you wish to use sub-domains.'=>'Kuna kasutad nime %1$s, peab WordPressi võrgustikus kasutama alamkatalooge. Alamdomeenide kasutamiseks peaksid tarvitama nime %2$s.','Because your installation is in a directory, the sites in your WordPress network must use sub-directories.'=>'Kuna WordPress paigaldatakse kataloogi, peavad võrgustiku saidid asuma selle alamkataloogides.','Because your installation is not new, the sites in your WordPress network must use sub-domains.'=>'Kuna see ei ole uus paigaldus, peavad sinu WordPressi võrgustiku saidid kasutama alamdomeene.','Both sites and user accounts can be registered'=>'Saab registreerida nii saite kui kasutajakontosid','Broken Theme:'=>'Katkine teema:','Cannot add user.'=>'Kasutajat ei saa lisada.','Cannot create an empty site.'=>'Tühja saiti ei saa luua.','Cannot create an empty user.'=>'Tühja kasutajat ei saa luua.','Child theme of %s'=>'Teema %s alamteema','Choose subdomains or subdirectories; this can only be switched afterwards by reconfiguring your installation. Fill out the network details, and click Install. If this does not work, you may have to add a wildcard DNS record (for subdomains) or change to another setting in Permalinks (for subdirectories).'=>'Vali kas alamdomeenid või alamkataloogid - seda saab hiljem vahetada vaid palgaldust uuesti häälestades. Sisesta enda võrguinfo ja klõpsa paigalduskäsku. Kui see ei tööta, võib olla vaja lisada nimeserveri  hulgikirje (alamdomeenide jaoks) või muuta püsiviidete sätteid (alamkataloogide jaoks).','Clicking on bold headings can re-sort this table.'=>'Klõpsamine rasvases kirjas pealkirjadel muudab tabeli sortimist.','Complete the following steps to enable the features for creating a network of sites.'=>'Saidivõrgustiku käivitamiseks teee järgmised tegevused.','Confirm your action'=>'Kinnita oma tegevus','Create a Network of WordPress Sites'=>'Loo WordPressi veebilehtede võrgustik','Dashboard leads to the Dashboard for that site.'=>'Töölaud viib selle saidi töölauale.','Deactivate, Archive, and Spam which lead to confirmation screens. These actions can be reversed later.'=>'Väljalülitamine, arhiveerimine ja rämpsuksmärkimine küsivad kinnitust. Neid tegevusi saab hiljem tagasi võtta.','Default Language'=>'Vaikimisi keel','Delete Theme'=>'Kustuta teema','Delete Themes'=>'Kustuta teemad','Delete which is a permanent action after the confirmation screens.'=>'Kustutamine on kinnituse andmise järel lõplik.','Deleted'=>'Kustutatud','Deleted <span class="count">(%s)</span>'=>'Kustutatud <span class="count">(%s)</span>' . "\0" . 'Kustutatud <span class="count">(%s)</span>','Disable'=>'Lülita välja','Disable %s'=>'Lülita %s välja','Domain'=>'Domeen','Duplicated username or email address.'=>'Kasutajanimi või e-postiaadress on juba olemas.','Edit Site: %s'=>'Muuda saiti: %s','Enable'=>'Lülita sisse','Enable %s'=>'Lülita %s sisse','Enable administration menus'=>'Luba haldamismenüüd','Enable menus'=>'Luba menüüsid','Enabling the Network'=>'Võrgustiku sisselülitamine','Enter the username and email.'=>'Sisesta kasutajanimi ja epost.','Enter the username of an existing user.'=>'Sisesta olemasoleva kasutaja kasutajanimi.','Error: The network could not be created.'=>'VIGA: võrgustikku ei õnnestunud luua.','Fill in the information below and you&#8217;ll be on your way to creating a network of WordPress sites. Configuration files will be created in the next step.'=>'WordPressi saitide võrgustiku loomiseks täida allolevad väljad. Häälestusfailide loomine on järgmine samm.','Filter site themes list'=>'Filtreeri teemade nimekirja','Filter site users list'=>'Filtreeri teemade nimekirja','First Comment'=>'Esimene kommentaar','First Comment Author'=>'Esimese kommentaari autor','First Comment Email'=>'Esimese kommentaari e-postiaadress','First Comment URL'=>'Esimese kommentaari URL','First Page'=>'Esimene leht','From here you can:'=>'Siit saad:','Hover over any user on the list to make the edit links appear. The Edit link on the left will take you to their Edit User profile page; the Edit link on the right by any site name goes to an Edit Site screen for that site.'=>'Liigu hiirega nimekirjas kasutaja nime peale nägemaks toimingukäskude linke. Vasakul asuv Muuda link viib su vastava kasutaja profiililehete toimetama; saidinimest paremal olev Muuda link viib vastava saidi seadistamise lehele.','Hovering over each site reveals seven options (three for the primary site):'=>'Kursori viimine saidi kohale näitab seitset (peasaidi puhul kolme) valikut:','If %1$s is disabled, ask your administrator to enable that module, or look at the <a href="%2$s">Apache documentation</a> or <a href="%3$s">elsewhere</a> for help setting it up.'=>'Kui %1$s on välja lülitatud, palu see halduril sisse lülitada või uuri kas <a href="%2$s">Apache dokumentatsioonist</a> or <a href="%3$s">mujalt</a>, kuidas seda seadistatakse.','If a version update to core has not happened, clicking this button will not affect anything.'=>'Kui süsteemi kesksete osade uuendust ei ole toimunud, siis selle nupu klõpsamine mingit mõju ei oma.','If registration is disabled, please set %1$s in %2$s to a URL you will redirect visitors to if they visit a non-existent site.'=>'Kui kasutajate registreerumine ei ole lubatud,  suuna %2$s-s  %1$s ümber lehele, mis teavitab olematust saidist.','If the admin email for the new site does not exist in the database, a new user will also be created.'=>'Kui uue  saidi halduri e-postiaadressi andmebaasis pole, luuakse ka uus kasutaja.','If the network admin disables a theme that is in use, it can still remain selected on that site. If another theme is chosen, the disabled theme will not appear in the site&#8217;s Appearance > Themes screen.'=>'Kui võrgustikuhaldur lülitab välja parajasti mõnel saidil kasutuses oleva teema, saab sellel saidil seda edasi kasutada. Kui seal aga valitakse teine teema, siis eelmine peale seda enam loetellu ei ilmu.','If this process fails for any reason, users logging in to their sites will force the same update.'=>'Kui see protsess mingil põhjusel nurjuba, siis tehakse sama uuendus igal veebilehel, kuhu kasutaja sisse logib.','If you want to ban domains from site registrations. One domain per line.'=>'Kui soovitakse keelata saidi registreerimine teatud domeenidest. Domeeninimed tuleb kirjutada eraldi ridadele.','If you want to limit site registrations to certain domains. One domain per line.'=>'Kui soovitakse piirata uute saitide registreerimist kindlate domeenidega. Domeeninimed tuleb kirjutada eraldi ridadele.','If your browser does not start loading the next page automatically, click this link:'=>'Kui sinu veebilehitseja ei ava järgmist lehte automaatselt, klõpsa seda viidet:','Install and activate themes or plugins'=>'Paigalda ja aktiveeri teemasid või pluginaid','Installed Themes'=>'Paigaldatud teemad','Invalid site ID.'=>'Vigane saiditunnus.','It looks like the Apache %s module is not installed.'=>'Näib, et Apache moodulit %s ei ole paigaldatud.','It seems your network is running with Nginx web server. <a href="%s">Learn more about further configuration</a>.'=>'Tundub, et su server kasutab Nginxi veebiserverit. <a href="%s">Loe lisaks selle seadistuse kohta</a>.','Language Settings'=>'Keeleseaded','Last Updated'=>'Viimati uuendatud','Limit total size of files uploaded to %s MB'=>'Piira üles laaditud failide kogumaht %s megabaidiga','Limited Email Registrations'=>'Piiratud registreerimine e-posti teel','Logged in users may register new sites'=>'Sisselogitud kasutajad saavad registreerida uusi saite','Main'=>'Peamine','Mature'=>'Valmis','Mature <span class="count">(%s)</span>'=>'Valmis <span class="count">(%s)</span>' . "\0" . 'Valmis <span class="count">(%s)</span>','Max upload file size'=>'Üleslaaditava faili maksimumsuurus','Menu setting enables/disables the plugin menus from appearing for non super admins, so that only super admins, not site admins, have access to activate plugins.'=>'Menüüsäte lülitab madalama taseme haldurite jaoks pluginamenüüd sisse-välja. Nii võib lubada ligipääsu pluginate käivitamisele vaid peahalduritele (mitte aga üksikute saitide omadele).','Missing email address.'=>'E-postiaadress on puudu.','Missing or invalid site address.'=>'Saidi aadress on puudu või vigane.','Missing site title.'=>'Veebisaidi pealkiri puudub.','Modify global network settings'=>'Muuda võrgustikuüleseid seadeid','Network'=>'Võrgustik','Network Admin Email'=>'Võrgustikuhalduri e-post','Network Details'=>'Võrgustiku üksikasjad','Network Disable'=>'Keela võrgustikus','Network Disable %s'=>'Lülita %s kõikjal välja','Network Enable %s'=>'Lülita %s kõikjal sisse','Network Settings'=>'Võrgustiku seaded','Network Title'=>'Võrgustiku nimi','Network configuration authentication keys'=>'Võrgusätete autentimisvõtmed','Network configuration rules for %s'=>'Võrgusätted %s jaoks.','Network enabled themes are not shown on this screen.'=>'Võrgustikus lubatud teemasid sellel lehel ei kuvata.','Never'=>'Mitte kunagi','New Site Settings'=>'Uue saidi sätted','New registrations settings'=>'Uute kasutajate registreerimissätted','New site settings are defaults applied when a new site is created in the network. These include welcome email for when a new site or user account is registered, and what&#8127;s put in the first post, page, comment, comment author, and comment URL.'=>'Uue saidi sätted on vaikimisi valikud, mida kasutab iga võrgustikku loodav uus veebisait. Sinna kuuluvad näiteks uue saidi või kasutajakonto loomisel saadetav tervitustekst, samuti esimese postituse ja lehe sätted nagu vaikimisi kommentaar, selle autor ja tema URL.','Next Sites'=>'Järgmised veebilehed','No sites found.'=>'Saite pole.','No theme selected.'=>'Ühtegi teemat pole valitud.','No themes are currently available.'=>'Teemad pole hetkel saadaval.','No themes found.'=>'Teemasid ei leitud.','No, return me to the theme list'=>'Ei, tagasi teemade nimekirja paluks','Once the network is created, you may reactivate your plugins.'=>'Peale võrgustiku loomist võid pluginad uuesti sisse lülitada.','Once you add this code and refresh your browser, multisite should be enabled. This screen, now in the Network Admin navigation menu, will keep an archive of the added code. You can toggle between Network Admin and Site Admin by clicking on the Network Admin or an individual site name under the My Sites dropdown in the Toolbar.'=>'Kui oled koodi lisanud ja veebilehitsejat värskendanud, peaks mitme veebilehe režiim toimima. Nüüd on see võrguhalduri menüü ning säilitab ka lisatud koodilõigud.  Võrgustikuhalduri ja veebilehe halduri liideste vahel lülitumiseks klõpsa kas Võrguhaldurile või tööriistaribal Minu veebilehtede all olevale lehe nimele.','Once you complete these steps, your network is enabled and configured. You will have to log in again.'=>'Kui see kõik on tehtud, on võrgustik häälestatud ja sisse lülitatud. Logi uuesti sisse.','Only lowercase letters (a-z), numbers, and hyphens are allowed.'=>'Lubatud on vaid väiketähed, numbrid ja sidekriips.','Only use this screen once you have updated to a new version of WordPress through Updates/Available Updates (via the Network Administration navigation menu or the Toolbar). Clicking the Upgrade Network button will step through each site in the network, five at a time, and make sure any database updates are applied.'=>'Kasuta seda ainult siis, kui oled uuendanud WordPressi versiooni kasutades Uuendused / Saadaval uuendused (Võrgustikuadminni navigatsioonimenüüs või Tööriistaribal). Kui klõpsad Uuenda võrgustikku käiakse kogu võrgustik viie saidi kaupa läbi ja uuendatakse kõigi saitide andebaasid.','Operational Settings'=>'Töösätted','Operational settings has fields for the network&#8217;s name and admin email.'=>'Töösätete alt leiad väljad võrgustiku nime ja halduri e-aadressi jaoks.','Path'=>'Kataloogitee','Please <a href="%s">deactivate your plugins</a> before enabling the Network feature.'=>'Enne võrgustiku käivitamist palun <a href="%s">lülita pluginad välja</a>.','Please choose whether you would like sites in your WordPress network to use sub-domains or sub-directories.'=>'Palun vali, kas WordPressi võrgustikus tuleb kasutada alamdomeene või -katalooge.','Please complete the configuration steps. To create a new network, you will need to empty or remove the network database tables.'=>'Palun läbi kõik seadistussammud. Uue võrgustiku loomiseks pead selle andmebaasitabelid kas tühjendama või kustutama.','Please make sure the Apache %s module is installed as it will be used at the end of this installation.'=>'Veendu, et Apache %s moodul on olemas, kuna see paigaldus vajab seda hiljem.','Public <span class="count">(%s)</span>'=>'Avalik <span class="count">(%s)</span>' . "\0" . 'Avalikud <span class="count">(%s)</span>','Quick Tasks'=>'Kiired toimingud','Registration Settings'=>'Registreerimissätted','Registration is disabled'=>'Registreerumine on välja lülitatud','Registration notification'=>'Registreerimismärguanne','Registration settings can disable/enable public signups. If you let others sign up for a site, install spam plugins. Spaces, not commas, should separate names banned as sites for this network.'=>'Registreerimissätetest saad lugejate registreerumist sisse-välja lülitada. Kui lubad teistel registreeruda, peaksid paigaldama rämpsu filtreeriva plugina.  Keelatud  saidinimed tuleb eraldada tühikute, mitte komadega.','Select a user to change role.'=>'Vali kasutaja kelle rolli soovid muuta.','Select a user to remove.'=>'Vali kasutaja kelle soovid eemaldada.','Send the network admin an email notification every time someone registers a site or user account'=>'Saada võrgustikuhaldurile teade iga kord, kui keegi registreerib saidi või kasutajakonto','Server Address'=>'Serveri aadress','Set site attributes'=>'Määra saidi atribuudid','Site activated.'=>'Sait lülitati sisse.','Site added. <a href="%1$s">Visit Dashboard</a> or <a href="%2$s">Edit Site</a>'=>'Sait lisatud. <a href="%1$s">Külasta töölauda</a> või <a href="%2$s">Muuda saiti</a>','Site archived.'=>'Sait arhiveeriti.','Site deactivated.'=>'Sait lülitati välja.','Site deleted.'=>'Sait kustutati.','Site info updated.'=>'Saidi info uuendatud.','Site marked as spam.'=>'Sait märgiti rämpsuks.','Site options updated.'=>'Saidi seaded uuendatud.','Site removed from spam.'=>'Sait eemaldati rämpsuloendist.','Site themes list'=>'Teemade nimekiri','Site themes list navigation'=>'Teemade nimekirjas liikumine','Site unarchived.'=>'Sait eemaldati arhiivist.','Site upload space'=>'Saidi üleslaadimisruum','Site users list'=>'Kasutajate nimekiri','Site users list navigation'=>'Kasutajate nimekirjas liikumine','Sites deleted.'=>'Veebilehed kustutati.','Sites list'=>'Saitide nimekiri','Sites list navigation'=>'Saitide nimekirjas liikumine','Sites marked as spam.'=>'Veebilehed märgiti rämpsuks.','Sites removed from spam.'=>'Veebilehed eemaldati rämpsuloendist.','Size in kilobytes'=>'Maht kilobaitides','Sorry, you are not allowed to add sites to this network.'=>'Vabandust, sul ei ole õigust sellesse võrgustikku veebisaite lisada.','Sorry, you are not allowed to change the current site.'=>'Sul ei ole õigust seda saiti muuta.','Sorry, you are not allowed to change themes automatic update settings.'=>'Vabandust, sul ei ole õigust teemade automaatuuendust seadistada.','Sorry, you are not allowed to delete that site.'=>'Vabandust, sul ei ole õigust seda veebisaiti kustutada.','Sorry, you are not allowed to delete the site %s.'=>'Sul ei ole lubatud saiti %s kustutada.','Sorry, you are not allowed to delete themes for this site.'=>'Vabandust, sul pole õigust selle veebisaidi teemasid kustutada.','Sorry, you are not allowed to edit this site.'=>'Vabandust, sul ei ole õigust seda veebisaiti muuta.','Sorry, you are not allowed to manage network themes.'=>'Vabandust, sul ei ole õigust selle võrgustiku teemasid hallata.','Sorry, you are not allowed to manage themes for this site.'=>'Vabandust, sul ei ole õigust sellel veebisaidil teemasid hallata.','Sub-directories'=>'Alamkataloogid','Sub-directory Installation'=>'Alamkataloogi paigaldus','Sub-domain Installation'=>'Alamdomeeni paigaldus','Sub-domains'=>'Alamdomeenid','Subdirectory networks may not be fully compatible with custom wp-content directories.'=>'Alamkataloogidega võrgustikud ei pruugi ühilduda kohandatud wp-content-kataloogidega.','Super Admin <span class="count">(%s)</span>'=>'Peahaldur <span class="count">(%s)</span>' . "\0" . 'Peahaldurid <span class="count">(%s)</span>','Super admins can no longer be added on the Options screen. You must now go to the list of existing users on Network Admin > Users and click on Username or the Edit action link below that name. This goes to an Edit User page where you can check a box to grant super admin privileges.'=>'Superhaldureid ei saa enam Seadete lehe kaudu lisada. Sa pead minema olemasolevate kasutajate nimistusse Võrgustiku haldur > Kasutajad, klõpsama kasutajanimel või selle all oleval Muuda lingil. Selle tulemusena satud Muuda kasutajat lehele kus on võimalik märkida superhalduri õiguse lahter.','Table ordered by Last Updated.'=>'Tabel viimaste uuenduste alusel.','Table ordered by Site Domain Name.'=>'Tabel saidi domeeninime alusel.','Table ordered by Site Path.'=>'Tabel saidi asukoha alusel.','Table ordered by Site Registered Date.'=>'Tabel saidi registreerimiskuupäeva alusel.','Table ordered by Theme Name.'=>'Tabel teema nime alusel.','Table ordered by User Registered Date.'=>'Tabel kasutaja registreerimiskuupäeva alusel.','That&#8217;s all, stop editing! Happy publishing.'=>'Kõik, rohkem pole vaja muuta. Head avaldamist.','The Network creation panel is not for WordPress MU networks.'=>'Võrgustiku loomise paneel ei sobi WordPress MU võrgustikele.','The Right Now widget on this screen provides current user and site counts on your network.'=>'Moodul Ülevaade kuvab võrgustiku saitide ja kasutajate arvu.','The URL for the first comment on a new site.'=>'Uue saidi esimese kommentaari URL.','The author of the first comment on a new site.'=>'Uue saidi esimese kommentaari autor.','The bulk action will permanently delete selected users, or mark/unmark those selected as spam. Spam users will have posts removed and will be unable to sign up again with the same email addresses.'=>'See valik kustutab valitud kasutajad jäädavalt või märgib valitud kasutajad rämpsuks (uuesti valimine tühistab selle). Rämpskasutajate kõik postitused kustutatakse ja nad ei saa enam sama e-postiaadressiga sisse logida.','The choice of subdirectory sites is disabled if this setup is more than a month old because of permalink problems with &#8220;/blog/&#8221; from the main site. This disabling will be addressed in a future version.'=>'Alamkataloogide-põhine lahendus ei toimi, kui tarkvara on vanem kui kuu aega, kuna esineb probleem püsiviidetega. See loodetakse lahendada mõne järgmise väljalaske juures.','The constant %s cannot be defined when creating a network.'=>'Konstanti %s ei saa defineerida kui lood võrgustikku.','The domain or path entered conflicts with an existing username.'=>'Sisestatud domeen või kataloogitee läheb konflikti olemasoleva kasutajanimega.','The email address of the first comment author on a new site.'=>'Uue veebisaidi esimese kommentaari autori e-postiaadress.','The first comment on a new site.'=>'Uue saidi esimene kommentaar.','The first page on a new site.'=>'Uue saidi esimene leht.','The first post on a new site.'=>'Uue saidi esimene postitus.','The following words are reserved for use by WordPress functions and cannot be used as site names: %s'=>'Järgmised sõnad on reserveeritud WordPressi funktsioonidele ning neid ei saa veebisaitide nimedes kasutada: %s','The internet address of your network will be %s.'=>'Sinu võrgustiku internetiaadressiks on %s.','The main site in a sub-directory installation will need to use a modified permalink structure, potentially breaking existing links.'=>'Alamkataloogi paigaldatud võrgustiku peasait peab kasutama muudetud püsiviitestruktuuri, see aga võib olemasolevad viited katki teha.','The next screen for Network Setup will give you individually-generated lines of code to add to your wp-config.php and .htaccess files. Make sure the settings of your FTP client make files starting with a dot visible, so that you can find .htaccess; you may have to create this file if it really is not there. Make backup copies of those two files.'=>'Võrgu häälestuspaneel pakub sinu jaoks genereeritud programmiridu, mida võid kasutada wp-config.php ja .htaccess -failides. Kontrolli, et su FTP klient näitaks punktiga algavate nimedega faile. Kui aga .htaccess tõepoolest puudub, siis tasuks see luua. Tee neist kahest failist kindlasti varukoopiad.','The original configuration steps are shown here for reference.'=>'Siin on infoks kirjas algsed seadistussammud.','The requested action is not valid.'=>'Sellist toimingut ei ole teostatavate asjade nimekirjas.','The requested site does not exist.'=>'Sellist saiti ei ole olemas.','The site ID is used internally, and is not shown on the front end of the site or to users/viewers.'=>'Saiditunnust kasutatakse sisemiselt, avalikkusele seda ei näidata.','The username and a link to set the password will be mailed to this email address.'=>'Kasutajanimi ja link parooli seadmiseks saadetakse sellele e-mailiaadressile.','The welcome email sent to new site owners.'=>'Uue saidi omanikule saadetav tervituskiri.','The welcome email sent to new users.'=>'Uuele kasutajale saadetav tervituskiri.','Theme disabled.'=>'Teema välja lülitatud.','Theme enabled.'=>'Teema sisse lülitatud.','Themes can be enabled on a site by site basis by the network admin on the Edit Site screen (which has a Themes tab); get there via the Edit action link on the All Sites screen. Only network admins are able to install or edit themes.'=>'Võrgustiku haldur saab teemasid sait-haaval lubada Muuda saiti lehe Teemade sakil. Sinna pääseb valides Muuda-lingi lehelt Kõik saidid. Teemasid saavad lisada ja muuta ainult võrgustiku haldurid.','Themes list navigation'=>'Teemade nimekirjas liikumine','There is a pending change of the network admin email to %s.'=>'Võrgustiku administraatori e-maili aadress %s on muutmisel.','There was an error creating the user.'=>'Kasutaja loomisel ilmnes viga.','These themes may be active on other sites in the network.'=>'Need teemad võivad olla kasutusel võrgustiku teistel saitidel.','These unique authentication keys are also missing from your %s file.'=>'Need autentimisvõtmed puuduvad ka failist %s.','This is the main table of all sites on this network. Switch between list and excerpt views by using the icons above the right side of the table.'=>'See on kõigi selle võrgustiku veebilehtede tabel. Ülal paremal asuvate ikoonide abil saad lülituda kas nimekirja- või katkendivaatele.','This screen allows you to configure a network as having subdomains (<code>site1.example.com</code>) or subdirectories (<code>example.com/site1</code>). Subdomains require wildcard subdomains to be enabled in Apache and DNS records, if your host allows it.'=>'Siit saad häälestada alamdomeene (<code>osakond.firma.ee</code>) ja alamkatalooge (<code>firma.ee/osakond</code>). Alamdomeenide kasutamine eeldab vastavaid sätteid Apache ja nimeserveri seadetes, kui sinu võrguteenusepakkuja sellist võimalust pakub.','This screen enables and disables the inclusion of themes available to choose in the Appearance menu for each site. It does not activate or deactivate which theme a site is currently using.'=>'Siit saad kõigi saitide teemadeloetellu teemasid lisada ja neid eemaldada. See ei muuda jooksvalt kasutatavat teemat.','This screen is for Super Admins to add new sites to the network. This is not affected by the registration settings.'=>'See leht lubab Superhalduril lisada võrgustikku uusi saite. Registreerimise seaded seda ei mõjuta.','This screen sets and changes options for the network as a whole. The first site is the main site in the network and network options are pulled from that original site&#8217;s options.'=>'Siit saad sättida kogu lehevõrgustiku seadistusi. Esimesena loodud saiti loetakse peamiseks ning võrgustiku valikud seatakse selle järgi.','This table shows all users across the network and the sites to which they are assigned.'=>'Tabelis on ära toodud kogu võrgustiku kasutajad ja veebilehed, kus nad on registreeritud.','This theme may be active on other sites in the network.'=>'See teema võib olla kasutusel võrgustiku teistel saitidel.','This unique authentication key is also missing from your %s file.'=>'See autentimisvõti puudub ka failist %s.','To add a new site, <strong>click Create a New Site</strong>.'=>'Uue saidi loomiseks <strong>klõpsa Loo uus sait</strong>.','To add a new user, <strong>click Create a New User</strong>.'=>'Uue kasutaja lisamiseks <strong>klõpsa Loo uus kasutaja</strong>.','To make your installation more secure, you should also add:'=>'Paigalduse paremaks turvamiseks peaksid ka lisama:','To search for a site, <strong>enter the path or domain</strong>.'=>'Saidi otsimiseks <strong>sisesta kataloogirada või domeeninimi</strong>.','To search for a user or site, use the search boxes.'=>'Kasutaja või saidi leidmiseks kasuta vastavat otsingukasti.','To search for a user, <strong>enter an email address or username</strong>. Use a wildcard to search for a partial username, such as user&#42;.'=>'Kasutaja otsimiseks <strong>sisesta epost või kasutajanimi</strong>. Kasutajanime osa järgi saab otsida kasutades metamärki, nt kasutaja&#42;.','Unarchive'=>'Arhiivist välja','Update your network'=>'Uuenda oma võrgustikku','Updates'=>'Uuendused','Upgrade Network'=>'Uuenda võrgustikku','Upload Settings'=>'Üleslaadimissätted','Upload file types'=>'Üleslaaditavate failide tüübid','Upload settings control the size of the uploaded files and the amount of available upload space for each site. You can change the default value for specific sites when you edit a particular site. Allowed file types are also listed (space separated only).'=>'Üleslaadimissätted määravad üles laaditava faili lubatud mahu ja iga saidi üleslaadimiste lubatud kogumahu. Neid väärtusi saad muuta iga saidi juures eraldi. Samuti on siin loetletud lubatud failitüübid (need tuleb eraldada tühikutega).','User accounts may be registered'=>'Saab registreerida kasutajakontosid','User could not be added to this site.'=>'Kasutaja lisamine saidile ebaõnnestus.','User created.'=>'Kasutaja loodud.','User is already a member of this site.'=>'Kasutaja juba on selle saidi liige.','Users are not allowed to register these sites. Separate names by spaces.'=>'Kasutajad ei saa nende nimedega veebilehti registreerida. Eralda nimed tühkutega.','Users deleted.'=>'Kasutajad kustutati.','Users marked as spam.'=>'Kasutajad märgiti rämpsuks.','Users removed from spam.'=>'Kasutajad eemaldati rämpsunimekirjast.','Users who are signed up to the network without a site are added as subscribers to the main or primary dashboard site, giving them profile pages to manage their accounts. These users will only see Dashboard and My Sites in the main navigation until a site is created for them.'=>'Kasutajad, kes on registreerunud võrgustikku ilma saiti loomata, lisatakse peasaidile lugeja rollis, andes neile enda konto haldamiseks kasutajaprofiili. Need kasutajad näevad ainult peamenüü valikuid Töölaud ja Minu saidid, kuni neile luuakse omaenda sait.','Visit Theme Site'=>'Külasta teema veebi','Visit theme site for %s'=>'Mine %s teema kodulehele','Visit to go to the front-end site live.'=>'Vaata saiti väljast.','Warning! Problem updating %1$s. Your server may not be able to connect to sites running on it. Error message: %2$s'=>'Hoiatus: %1$s uuendamisel tekkis probleem. Sinu server ei pruugi suuta enda saitidele ligi pääseda. Veateade: %2$s','Warning! User cannot be modified. The user %s is a network administrator.'=>'Hoiatus: kasutajat ei saa muuta. Kasutaja %s on võrgustikuhaldur.','Welcome Email'=>'Tervituskiri','Welcome User Email'=>'Uue kasutaja tervituskiri','Welcome to the Network installation process!'=>'Tere tulemast võrgustiku paigaldusse!','Welcome to your Network Admin. This area of the Administration Screens is used for managing all aspects of your Multisite Network.'=>'Teretulemast Võrgustikuadminni. Siin saad hallata kõiki oma Multisite-võrgustiku seadistusi.','What would you like to call your network?'=>'Kuidas soovid enda võrgustikku kutsuda?','WordPress has been updated! Next and final step is to individually upgrade the sites in your network.'=>'WordPress on uuendatud! Järgmine ja viimane samm on uuendada ükshaaval ära kõik võrgustiku veebisaidid.','Y/m/d g:i:s a'=>'d.m.Y H:i:s','Yes, delete these themes'=>'Jah, kustuta need teemad','Yes, delete this theme'=>'Jah, kustuta see teema','You are about to activate the site %s.'=>'Oled sisse lülitamas saiti %s.','You are about to archive the site %s.'=>'Oled arhiveerimas saiti %s.','You are about to deactivate the site %s.'=>'Oled välja lülitamas saiti %s.','You are about to delete the following sites:'=>'Oled kustutamas järgmisi saite:','You are about to delete the site %s.'=>'Oled kustutamas saiti %s.','You are about to mark the site %s as mature.'=>'Oled märkimas saiti %s täiskasvanutele mõelduks.','You are about to mark the site %s as not mature.'=>'Oled eemaldamas saidilt %s märget täiskasvanutele mõeldud sisu kohta.','You are about to mark the site %s as spam.'=>'Oled märkimas saiti %s rämpsuks.','You are about to remove the following theme:'=>'Oled kustutamas järgmist teemat:','You are about to remove the following themes:'=>'Oled kustutamas järgmisi teemasid:','You are about to unarchive the site %s.'=>'Oled võtmas arhiivist välja saiti %s.','You are about to unspam the site %s.'=>'Sa eemaldad saidilt %s rämpsumärgistuse.','You can also go to the user&#8217;s profile page by clicking on the individual username.'=>'Kasutaja profiililehele pääseb kasutajanimele klõpsates.','You can make an existing user an additional super admin by going to the Edit User profile page and checking the box to grant that privilege.'=>'Olemasolevat kasutajat saab määrata peahalduriks tema profiililehel vastavat märkeruutu klõpsates.','You can sort the table by clicking on any of the table headings and switch between list and excerpt views by using the icons above the users list.'=>'Tabelit saab sorteerida veerupäistele klõpsates, ülal paremal asuvate ikoonidega saab lülituda nimekirja- ja katkendivaate vahel.','You cannot change this later.'=>'Seda hiljem enam muuta ei saa.','You cannot delete a theme while it is active on the main site.'=>'Sa ei saa kustutada teemat, mis on põhisaidi teemana kasutusel.','You cannot install a network of sites with your server address.'=>'Sellele aadressile ei saa saitide võrgustikku luua.','You cannot use port numbers such as %s.'=>'Pordinumbreid nagu %s ei saa kasutada.','You must define the %1$s constant as true in your %2$s file to allow creation of a Network.'=>'Võrgustiku loomise lubamiseks pead määrama failis %2$s konstandile %1$s tõese väärtuse.','You should back up your existing %1$s and %2$s files.'=>'Peaksid varundama olemasolevad %1$s ja %2$s failid.','You should back up your existing %s file.'=>'Peaksid varundama olemasoleva %s faili.','You should consider changing your site domain to %1$s before enabling the network feature. It will still be possible to visit your site using the %3$s prefix with an address like %2$s but any links will not have the %3$s prefix.'=>'Enne võrgustiku sisselülitamist peaksid muutma enda domeeni %1$s peale. %3$s prefiksiga saab sinu veebisaidi kätte (aadressiga %2$s), aga veebiviidetel prefiksit %3$s ei ole.','You will need a wildcard DNS record if you are going to use the virtual host (sub-domain) functionality.'=>'Kui tahad kasutada alamdomeeniga virtuaalservereid, pead need seadistama nimeserveris.','Your email address.'=>'Sinu e-aadress.','https://wordpress.org/documentation/article/nginx/'=>'https://wordpress.org/documentation/article/nginx/','siteActivate'=>'Aktiveeri','siteMark as spam'=>'Märgi rämpsuks','siteNot Spam'=>'Pole rämps','siteNot spam'=>'Pole rämps','sitePublic'=>'Avalik','siteRegistered'=>'Registreeritud','siteSpam'=>'Rämps','sitesAll <span class="count">(%s)</span>'=>'Kõik saidid: <span class="count">(%s)</span>' . "\0" . 'Kõik saidid: <span class="count">(%s)</span>','sitesSpam <span class="count">(%s)</span>'=>'Rämps <span class="count">(%s)</span>' . "\0" . 'Rämps <span class="count">(%s)</span>','subdirectory exampleslike <code>%1$s/site1</code> and <code>%1$s/site2</code>'=>'näiteks <code>%1$s/sait1</code> ja <code>%1$s/sait2</code>','subdomain exampleslike <code>site1.%1$s</code> and <code>site2.%1$s</code>'=>'näiteks <code>sait1.%1$s</code> ja <code>sait2.%1$s</code>','theme%1$s by %2$s'=>'%1$s, autor %2$s','themesAll <span class="count">(%s)</span>'=>'Kõik <span class="count">(%s)</span>' . "\0" . 'Kõik <span class="count">(%s)</span>','themesBroken <span class="count">(%s)</span>'=>'Katkine teema <span class="count">(%s)</span>' . "\0" . 'Katkised teemad <span class="count">(%s)</span>','themesDisabled <span class="count">(%s)</span>'=>'Väljalülitatud teema <span class="count">(%s)</span>' . "\0" . 'Väljalülitatud teemad <span class="count">(%s)</span>','themesEnabled <span class="count">(%s)</span>'=>'Sisselülitatud teema <span class="count">(%s)</span>' . "\0" . 'Sisselülitatud teemad <span class="count">(%s)</span>','themesUpdate Available <span class="count">(%s)</span>'=>'Uuendatav teema <span class="count">(%s)</span>' . "\0" . 'Uuendatavad teemad <span class="count">(%s)</span>','userMark as spam'=>'Märgi rämpsuks','userNot spam'=>'Pole rämps','userRegistered'=>'Registreeritud','verb; siteArchive'=>'Arhiivi']];
