return ['po-revision-date'=>'2025-09-19 18:17:59+0000','mime-version'=>'1.0','content-type'=>'text/plain; charset=UTF-8','content-transfer-encoding'=>'8bit','plural-forms'=>'nplurals=3; plural=(n % 10 == 1 && (n % 100 < 11 || n % 100 > 19)) ? 0 : ((n % 10 >= 2 && n % 10 <= 9 && (n % 100 < 11 || n % 100 > 19)) ? 1 : 2);','x-generator'=>'GlotPress/4.0.1','language'=>'lt','project-id-version'=>'WordPress - 6.8.x - Development - Administration - Network Admin','messages'=>['%1$s &#8211; %2$s'=>'%1$s &#8211; %2$s','%s KB'=>'%s KB','%s Sites'=>'%s Tinklalapiai','%s theme deleted.'=>'Ištrinta temų: %s ' . "\0" . 'Ištrinta temų: %s ' . "\0" . 'Ištrinta temų: %s ','%s theme disabled.'=>'Išjungta temų: %s ' . "\0" . 'Išjungta temų: %s ' . "\0" . 'Išjungta temų: %s ','%s theme enabled.'=>'Įjungta temų: %s ' . "\0" . 'Įjungta temų: %s ' . "\0" . 'Įjungta temų: %s ','%s theme will be auto-updated.'=>'%s išvaizdos tema bus atnaujinama automatiškai.' . "\0" . '%s išvaizdos temos bus atnaujinamos automatiškai.' . "\0" . '%s išvaizdos temų bus atnaujinama automatiškai.','%s theme will no longer be auto-updated.'=>'%s išvaizdos tema daugiau nebus atnaujinama automatiškai.' . "\0" . '%s išvaizdos temos daugiau nebus atnaujinamos automatiškai.' . "\0" . '%s išvaizdos temų daugiau nebus atnaujinama automatiškai.','<a href="https://codex.wordpress.org/Network_Admin_Themes_Screen">Documentation on Network Themes</a>'=>'<a href="https://codex.wordpress.org/Network_Admin_Themes_Screen">Tinklo temų dokumentacija</a>','<a href="https://codex.wordpress.org/Network_Admin_Users_Screen">Documentation on Network Users</a>'=>'<a href="https://codex.wordpress.org/Network_Admin_Users_Screen">Tinklo vartotojų dokumentacija</a>','<a href="https://developer.wordpress.org/advanced-administration/multisite/admin/">Documentation on the Network Admin</a>'=>'<a href="https://developer.wordpress.org/advanced-administration/multisite/admin/">Tinklo administratoriaus dokumentacija</a>','<a href="https://developer.wordpress.org/advanced-administration/multisite/admin/#network-admin-updates-screen">Documentation on Upgrade Network</a>'=>'<a href="https://developer.wordpress.org/advanced-administration/multisite/admin/#network-admin-updates-screen">Tinklo atnaujinimo dokumentacija</a>','<a href="https://developer.wordpress.org/advanced-administration/multisite/admin/settings/">Documentation on Network Settings</a>'=>'<a href="https://developer.wordpress.org/advanced-administration/multisite/admin/settings/">Tinklo nustatymų dokumentacija</a>','<a href="https://developer.wordpress.org/advanced-administration/multisite/create-network/">Documentation on Creating a Network</a>'=>'<a href="https://developer.wordpress.org/advanced-administration/multisite/create-network/">Tinklo sukūrimo dokumentacija</a>','<a href="https://wordpress.org/documentation/article/tools-network-screen/">Documentation on the Network Screen</a>'=>'<a href="https://wordpress.org/documentation/article/tools-network-screen/">Tinklo skydelio dokumentacija</a>','A new user will be created if the above email address is not in the database.'=>'Jei el. pašto adreso dar nėra duomenų bazėje, bus sukurtas naujas vartotojas.','A password reset link will be sent to the user via email.'=>'Slaptažodžio atstatymo nuoroda bus išsiųsta vartotojui el.paštu.','Active Child Theme'=>'Aktyvi potemė','Add New User'=>'Pridėti naują vartotoją','Add Site'=>'Sukurti naują tinklalapį','Add Site takes you to the screen for adding a new site to the network. You can search for a site by Name, ID number, or IP address. Screen Options allows you to choose how many sites to display on one page.'=>'Paspaudę „Pridėti tinklalapį“, būsite perkelti į naujo tinklalapio pridėjimo puslapį. Primename, kad galite ieškoti tinklalapio pagal Pavadinimą, ID ar IP adresą. Kortelėje „Ekrano nustatymai“ galėsite pasirinkti, kiek tinklalapių rodyti viename puslapyje.','Add User will set up a new user account on the network and send that person an email with username and password.'=>'Funkcija „Pridėti vartotoją“ tinkle sukurs naują paskyrą ir paštu nusiųs prisijungimo duomenis.','Add Users'=>'Pridėti vartotojų','Add and manage sites or users'=>'Kurti ir tvarkyti tinklalapius ir vartotojus','Add the designated lines of code to wp-config.php (just before <code>/*...stop editing...*/</code>) and <code>.htaccess</code> (replacing the existing WordPress rules).'=>'Add the designated lines of code to wp-config.php (just before <code>/*...stop editing...*/</code>) and <code>.htaccess</code> (replacing the existing WordPress rules).','Add the following to your %1$s file in %2$s <strong>above</strong> the line reading %3$s:'=>'Pridėkite šias eilutes į savo tinklalapio %1$s failą (jį rasite %2$s), <strong>virš</strong> eilutės %3$s:','Add the following to your %1$s file in %2$s, <strong>replacing</strong> other WordPress rules:'=>'Pridėkite tai į savo %1$s failą %2$s direktorijoje, <strong>pakeisdami</strong> kitas WordPress taisykles:','Addresses of Sites in your Network'=>'Tinklalapių adresai jūsų tinkle','Admin Email'=>'Administratoriaus el. paštas','All Sites'=>'Visi tinklalapiai','All done!'=>'Atlikta!','Allow new registrations'=>'Leidimas registruotis','Allow site administrators to add new users to their site via the "Users &rarr; Add User" page'=>'Leisti tinklalapių administratoriams pridėti naujus vartotojus į savo tinklalapius per „Vartotojai &rarr; Pridėti vartotoją“.','Allowed file types. Separate types by spaces.'=>'Leidžiami failų tipai. Atskirkite tipus tarpais.','An Edit link to a separate Edit Site screen.'=>'Redaguoti: būsite perkelti į tinklalapio nustatymų redagavimo puslapį.','An existing WordPress network was detected.'=>'Nustatytas jau esamas WordPress tinklas.','Archived'=>'Archyvuota','Archived <span class="count">(%s)</span>'=>'Suarchyvuoti <span class="count">(%s)</span>' . "\0" . 'Suarchyvuoti <span class="count">(%s)</span>' . "\0" . 'Suarchyvuoti <span class="count">(%s)</span>','Are you sure you want to delete these themes?'=>'Ar tikrai norite ištrinti šias temas?','Banned Email Domains'=>'Registracijų blokavimas','Banned Names'=>'Draudžiami pavadinimai','Because you are using %1$s, the sites in your WordPress network must use sub-directories. Consider using %2$s if you wish to use sub-domains.'=>'Jūs naudojate %1$s, todėl tinklalapiai Jūsų WordPress tinkle turi naudoti sub-direktorijas. Pagalvokite apie %2$s naudojimą, jei norite naudoti sub-domenus.','Because your installation is in a directory, the sites in your WordPress network must use sub-directories.'=>'Jūsų tinklalapis yra kataloge, todėl WordPress tinklo tinklalapiai turi naudoti subdirektorijas.','Because your installation is not new, the sites in your WordPress network must use sub-domains.'=>'Jūsų tinklalapis nėra naujas, todėl WordPress tinklo tinklalapiai turi naudoti sub-domenus.','Both sites and user accounts can be registered'=>'Laisva vartotojų ir tinklalapių registracija įjungta.','Broken Theme:'=>'Neveikianti tema:','Cannot add user.'=>'Nepavyksta pridėti vartotojo.','Cannot create an empty site.'=>'Negalima sukurti tuščio tinklalapio.','Cannot create an empty user.'=>'Negalima sukurti tuščio vartotojo.','Child theme of %s'=>'%s potemė','Choose subdomains or subdirectories; this can only be switched afterwards by reconfiguring your installation. Fill out the network details, and click Install. If this does not work, you may have to add a wildcard DNS record (for subdomains) or change to another setting in Permalinks (for subdirectories).'=>'Pasirinkite subdomenus ar subdirektorijas; vėliau tai bus galima pakeisti tik keičiant tinklo konfigūraciją. Suveskite tinklo informaciją, ir spauskite diegimo mygtuką. Jeigu diegimas nepavyksta, galbūt reikia pridėti „wildcard“ DNS įrašą (sub-domenams) arba pakeisti nuorodų nustatymus (sub-direktorijoms).','Complete the following steps to enable the features for creating a network of sites.'=>'Atlikite šiuos veiksmus, kad įjungtumėte funkcijas skirtas tinklo sukūrimui.','Confirm'=>'Patvirtinti','Confirm your action'=>'Patvirtinkite savo veiksmą','Create a Network of WordPress Sites'=>'Sukurkite WordPress tinklalapių tinklą','Dashboard leads to the Dashboard for that site.'=>'Skydelis: Būsite perkelti į pasirinkto tinklalapio administravimo sąsają.','Deactivate, Archive, and Spam which lead to confirmation screens. These actions can be reversed later.'=>'Išjungti, Archyvuoti, Brukalas: Būsite perkelti į pasirinkto veiksmo patvirtinimo puslapį. Vėliau tai galėsite atšaukti.','Default Language'=>'Numatytoji kalba','Delete Theme'=>'Ištrinti temą','Delete Themes'=>'Ištrinti temas','Delete this site permanently'=>'Ištrinti šį tinklalapį negrįžtamai','Delete which is a permanent action after the confirmation screen.'=>'Trinti: patvirtinus, šis veiksmas yra negrįžtamas.','Deleted'=>'Ištrinta','Deleted <span class="count">(%s)</span>'=>'Ištrinti <span class="count">(%s)</span>' . "\0" . 'Ištrinti <span class="count">(%s)</span>' . "\0" . 'Ištrinti <span class="count">(%s)</span>','Deleting a site is a permanent action that cannot be undone. This will delete the entire site and its uploads directory.'=>'Tinklalapio ištrynimas yra negrįžtamas veiksmas, kurio negalima atšaukti. Ištrinamas visas tinklalapis ir jo Uploads direktorija.','Disable %s'=>'Išjungti %s','Domain'=>'domenas','Duplicated username or email address.'=>'Vartotojo vardas arba el. pašto adresas jau naudojamas.','Edit Site: %s'=>'Tinklalapio redagavimas: %s','Enable %s'=>'Įjungti %s','Enable administration menus'=>'Įjungti administravimo meniu','Enable menus'=>'Įjungti meniu','Enabling the Network'=>'Suaktyvinamas tinklas','Enter the username and email.'=>'Įveskite vartotojo vardą ir el. pašto adresą.','Enter the username of an existing user.'=>'Įveskite esamo vartotojo vardą.','Fill in the information below and you&#8217;ll be on your way to creating a network of WordPress sites. Configuration files will be created in the next step.'=>'Užpildykite informaciją žemiau ir galėsite kurti WordPress tinklalapių tinklą. Kitame žingsnyje bus sukurti konfigūracijos failai.','Filter site themes list'=>'Filtruoti tinklalapio išvaizdos temų sąrašą','Filter site users list'=>'Filtruoti tinklalapio vartotojų sąrašą','First Comment'=>'Pirmasis komentaras','First Comment Author'=>'Pirmojo komentaro autorius','First Comment Email'=>'Pirmojo komentaro el.paštas','First Comment URL'=>'Pirmojo komentaro URL','First Page'=>'Pirmasis puslapis','From here you can:'=>'Čia jūs galite:','Hover over any user on the list to make the edit links appear. The Edit link on the left will take you to their Edit User profile page; the Edit link on the right by any site name goes to an Edit Site screen for that site.'=>'Ant vartotojo užvedę pelę, pamatysite funkcinius mygtukus. Jei paspausite prie vartotojo vardo esantį mygtuką „Redaguoti“, galėsite redaguoti jo profilį. Jei paspausite mygtuką „Redaguoti“, esantį prie tinklalapio pavadinimo, galėsite redaguoti pasirinktą tinklalapį.','Hovering over each site reveals seven options (three for the primary site):'=>'Užvedę pelės žymeklį ant tinklalapio, išvysite septynetą nustatymų (tris nustatymus pagrindiniam tinklalapiui):','If %1$s is disabled, ask your administrator to enable that module, or look at the <a href="%2$s">Apache documentation</a> or <a href="%3$s">elsewhere</a> for help setting it up.'=>'Jei %1$s yra išjungtas, paprašykite serverio administratoriaus įjungti šį modulį, skaitykite <a href="%2$s">Apache dokumentaciją</a> arba <a href="%3$s">kitur</a> apie šiuos nustatymus.','If a version update to core has not happened, clicking this button will not affect anything.'=>'Jei dar neįvykdėte WordPress sistemos atnaujinimo, paspaudus „Atnaujinti tinklą“, nebus įvykdyti jokie pakeitimai.','If registration is disabled, please set %1$s in %2$s to a URL you will redirect visitors to if they visit a non-existent site.'=>'Jeigu registracija išjungta, prašome nustatyti %1$s faile %2$s į adresą, kur norite nukreipti lankytojus, kai jie bando aplankyti neegzistuojantį tinklalapį.','If the admin email for the new site does not exist in the database, a new user will also be created.'=>'Jei nurodytas administratoriaus el. pašto adresas dar nebuvo užregistruotas, WordPress automatiškai sukurs ir naują vartotoją.','If the network admin disables a theme that is in use, it can still remain selected on that site. If another theme is chosen, the disabled theme will not appear in the site&#8217;s Appearance > Themes screen.'=>'Jei tinklo administratorius panaikina kieno nors naudojamą temą iš galimų temų sąrašo, tai nepadaro įtakos ją naudojantiems tinklalapiams: juose ir toliau bus galima naudoti šią temą iki tol, kol juose bus įjungta kuri nors kita tema.','If this process fails for any reason, users logging in to their sites will force the same update.'=>'Jei šis procesas dėl kokių nors priežasčių nutrūks, vartotojams jungiantis, atnaujinimas bus įvykdomas pakartotinai.','If you want to ban domains from site registrations. One domain per line.'=>'Blokuoti registracijas iš el. pašto adresų su nurodytais domenais. Vienas domenas vienoje linijoje.','If you want to limit site registrations to certain domains. One domain per line.'=>'Leisti registracijas tik iš el. pašto adresų su nurodytais domenais. Vienas domenas vienoje linijoje.','If your browser does not start loading the next page automatically, click this link:'=>'Jei jūsų naršyklė neatidaro kito puslapio automatiškai, paspauskite ant šios nuorodos:','Install and activate themes or plugins'=>'Įdiegti ir įgalinti temas ir įskiepius','Installed Themes'=>'Įdiegtos temos','Invalid site ID.'=>'Netinkamas tinklalapio ID.','It looks like the Apache %s module is not installed.'=>'Atrodo, kad Apache %s modulis nėra įdeigtas.','It seems your network is running with Nginx web server. <a href="%s">Learn more about further configuration</a>.'=>'Atrodo, kad naudojate Nginx HTTP serverį. <a href="%s">Skaityke plačiau apie tolimesnį konfigūravimą</a>.','Language Settings'=>'Kalbos nustatymai','Last Updated'=>'Paskutinis atnaujinimas','Limit total size of files uploaded to %s MB'=>'Failams įkelti serveryje suteikta vieta: %s MB','Limited Email Registrations'=>'Registracijų ribojimas','Logged in users may register new sites'=>'Prisijungę vartotojai gali laisvai kurti tinklalapius','Main'=>'Pagrindinis','Mature'=>'Mature','Mature <span class="count">(%s)</span>'=>'Suaugusiems <span class="count">(%s)</span>' . "\0" . 'Suaugusiems <span class="count">(%s)</span>' . "\0" . 'Suaugusiems <span class="count">(%s)</span>','Max upload file size'=>'Maks. įkeliamo failo dydis','Menu setting enables/disables the plugin menus from appearing for non super admins, so that only super admins, not site admins, have access to activate plugins.'=>'Meniu nustatymuose galite pasirinkti, ar leisti tinklo vartotojams naudoti įskiepius. Jei tokią galimybę išjungsite, įskiepius įjungti galės tik pagrindinis tinklo administratorius.','Missing email address.'=>'Nerastas el. pašto adresas.','Missing or invalid site address.'=>'Netinkamas arba nerastas tinklalapio adresas.','Missing site title.'=>'Trūksta tinklalapio pavadinimo.','Modify global network settings'=>'Keisti bendrus tinklo nustatymus','Network'=>'Tinklas','Network Admin Email'=>'Tinklo administratoriaus el.paštas','Network Details'=>'Tinklo detalės','Network Disable'=>'Išjungti tinkle','Network Disable %s'=>'Išjungti %s tinkle','Network Enable %s'=>'Įjungti %s tinkle ','Network Settings'=>'Tinklo nustatymai','Network Title'=>'Tinklo pavadinimas','Network configuration authentication keys'=>'Tinklo konfigūracijos autentifikavimo raktai','Network configuration rules for %s'=>'Tinklo konfigūravimo taisyklės, skirtos %s','Network enabled themes are not shown on this screen.'=>'Tinkle įjungtos temos šiame puslapyje nerodomos.','Never'=>'Niekada','New Site Settings'=>'Naujo tinklalapio nustatymai','New registrations settings'=>'Naujų registracijų nustatymai','New site settings are defaults applied when a new site is created in the network. These include welcome email for when a new site or user account is registered, and what&#8127;s put in the first post, page, comment, comment author, and comment URL.'=>'Naujo tinklalapio nustatymai nurodo numatytuosius nustatymus, kurie bus priskirti visiems naujiems tinklalapiams, įskaitant pasveikinimo laišką (kai užregistruojamas naujas tinklalapis arba vartotojo paskyra), taip pat pirmą įrašą, komentarą, jo autorių ir autoriaus interneto adresą.','Next Sites'=>'Kiti tinklalapiai','No sites found.'=>'Tinklalapių nerasta.','No theme selected.'=>'Tema nepasirinkta.','No themes are currently available.'=>'Šiuo metu nėra galimų išvaizdos temų.','No themes found.'=>'Temų nerasta.','No, return me to the theme list'=>'Ne, grąžinkite mane prie temų sąrašo','Once the network is created, you may reactivate your plugins.'=>'Kai tik tinklas bus sukurtas galėsite iš naujo aktyvuoti savo įskiepius.','Once you add this code and refresh your browser, multisite should be enabled. This screen, now in the Network Admin navigation menu, will keep an archive of the added code. You can toggle between Network Admin and Site Admin by clicking on the Network Admin or an individual site name under the My Sites dropdown in the Toolbar.'=>'Once you add this code and refresh your browser, multisite should be enabled. This screen, now in the Network Admin navigation menu, will keep an archive of the added code. You can toggle between Network Admin and Site Admin by clicking on the Network Admin or an individual site name under the My Sites dropdown in the Toolbar.','Once you complete these steps, your network is enabled and configured. You will have to log in again.'=>'Kai užbaigsite šiuos žingsnius, jūsų tinklas bus įjungtas ir sukonfiguruotas. Jums reikės prisijungti iš naujo.','Only lowercase letters (a-z), numbers, and hyphens are allowed.'=>'Leidžamos tIk mažosios lotyniškos raidės (a-z), skaitmenys ir brūkšneliai.','Only use this screen once you have updated to a new version of WordPress through Updates/Available Updates (via the Network Administration navigation menu or the Toolbar). Clicking the Upgrade Network button will step through each site in the network, five at a time, and make sure any database updates are applied.'=>'Naudokite šį įrankį tik tada, kai atnaujinsite WordPress versiją meniu skiltyje „Atnaujinimai/Galimi atnaujinimai“ (tinklo administravimo skydelyje arba įrankių juostoje). Paspaudus mygtuką „Atnaujinti tinklą“, bus atnaujinti visi tinkle esantys tinklalapiai, po penkis vienu metu. Įsitikinkite, kad nėra jokių duomenų bazės atnaujinimų.','Operational Settings'=>'Pagrindiniai nustatymai','Operational settings has fields for the network&#8217;s name and admin email.'=>'Pagrindiniai nustatymai turi tinklo pavadinimo ir administratoriaus el. pašto adreso laukelius.','Path'=>'Kelias','Please <a href="%s">deactivate your plugins</a> before enabling the Network feature.'=>'Prieš įjungdami tinklą <a href="%s">Išjunkite savo įskiepius</a>.','Please choose whether you would like sites in your WordPress network to use sub-domains or sub-directories.'=>'Prašome pasirinkti, ar norite savo WordPress tinklo tinklalapiams naudoti sub-domenus ar sub-direktorijas.','Please complete the configuration steps. To create a new network, you will need to empty or remove the network database tables.'=>'Prašome pabaigti konfiguravimo žingsnius. Norėdami sukurti naują tinklą, turėsite išvalyti arba ištrinti tinklo duomenų bazės lenteles.','Please make sure the Apache %s module is installed as it will be used at the end of this installation.'=>'Užtikrinkite, kad Apache %s modulis yra įdiegtas, nes jis bus reikalingas šio diegimo pabaigoje.','Public <span class="count">(%s)</span>'=>'Vieši <span class="count">(%s)</span>' . "\0" . 'Vieši <span class="count">(%s)</span>' . "\0" . 'Vieši <span class="count">(%s)</span>','Quick Tasks'=>'Greitos užduotys','Registration Settings'=>'Registracijos nustatymai','Registration is disabled'=>'Registracija išjungta','Registration notification'=>'Pranešimas apie registraciją','Registration settings can disable/enable public signups. If you let others sign up for a site, install spam plugins. Spaces, not commas, should separate names banned as sites for this network.'=>'Registracijos nustatymai įjungia/išjungia laisvą registraciją. Jeigu tokia galimybė įjungta, nepamirškite įsidiegti nuo brukalų saugančių įskiepių. Tarpeliais (ne kableliais!) galite atskirti subdomenus, kurių užregistruoti neleisite.','Select a user to change role.'=>'Pasirinkite vartotoją, kurio rolę norite pakeisti.','Select a user to remove.'=>'Pasirinkite vartotoją, kurį norite pašalinti.','Send the network admin an email notification every time someone registers a site or user account'=>'Kai kas nors užregistruoja vartotojo paskyrą arba tinklalapį, nusiųsti informacinį laišką tinklo administratoriui.','Server Address'=>'Serverio adresas','Set site attributes'=>'Nustatyti tinklalapio savybes','Site activated.'=>'Tinklalapis aktyvuotas.','Site added. <a href="%1$s">Visit Dashboard</a> or <a href="%2$s">Edit Site</a>'=>'Tinklalapis pridėtas. Eiti į <a href="%1$s">administravimo sąsają</a> arba <a href="%2$s">redaguoti tinklalapį</a>','Site archived.'=>'Tinklalapis suarchyvuotas.','Site deactivated.'=>'Tinklalapis išjungtas.','Site deleted.'=>'Tinklalapis ištrintas.','Site info updated.'=>'Tinklalapio informacija atnaujinta.','Site marked as spam.'=>'Tinklalapis pažymėtas kaip brukalas.','Site options updated.'=>'Tinklalapio nustatymai atnaujinti','Site removed from spam.'=>'Tinklalapis pašalintas iš brukalų sąrašo.','Site themes list'=>'Tinklalapio išvaizdos temų sąrašas','Site themes list navigation'=>'Tinklalapio išvaizdos temų sąrašo navigacija','Site unarchived.'=>'Tinklalapis išarchyvuotas.','Site upload space'=>'Failams skiriama vieta','Site users list'=>'Tinklalapio vartotojų sąrašas','Site users list navigation'=>'Tinklalapio vartotojų sąrašo navigacija','Sites deleted.'=>'Tinklalapiai ištrinti.','Sites list'=>'Tinklalapių sąrašas','Sites list navigation'=>'Tinklalapių sąrašo navigacija','Sites marked as spam.'=>'Tinklalapiai, pažymėti kaip brukalai.','Sites removed from spam.'=>'Tinklalapiai pašalinti iš brukalų sąrašo.','Size in kilobytes'=>'Dydis kilobaitais','Sorry, you are not allowed to add sites to this network.'=>'Deja, Jūs negalite pridėti tinklalapių į šį tinklą.','Sorry, you are not allowed to change the current site.'=>'Deja, Jūs negalite keisti šio tinklalapio.','Sorry, you are not allowed to change themes automatic update settings.'=>'Atsiprašome, Jūs neturite teisės keisti išvaizdos temų automatinio atnaujinimo nustatymus.','Sorry, you are not allowed to delete that site.'=>'Deja, Jūs negalite ištrinti šio tinklalapio.','Sorry, you are not allowed to delete the site %s.'=>'Deja, Jūs negalite ištrinti tinklalapio %s.','Sorry, you are not allowed to delete themes for this site.'=>'Deja, Jūs negalite trinti temų šiame tinklalapyje.','Sorry, you are not allowed to edit this site.'=>'Deja, Jūs negalite redaguoti šio tinklalapio.','Sorry, you are not allowed to manage network themes.'=>'Deja, Jūs negalite tvarkyti tinklo temų.','Sorry, you are not allowed to manage themes for this site.'=>'Deja, Jūs negalite tvarkyti šio tinklalapio temų.','Sub-directories'=>'Sub-direktorijos','Sub-directory Installation'=>'Svetainės subdirektorijose','Sub-domain Installation'=>'Svetainės subdomenuose','Sub-domains'=>'Sub-domenai','Subdirectory networks may not be fully compatible with custom wp-content directories.'=>'Sub-direktorijų tinklai gali būti nepilnai suderinami su nestandartine wp-content direktorijos vieta.','Super Admin <span class="count">(%s)</span>'=>'Super administratorius <span class="count">(%s)</span>' . "\0" . 'Super administratoriai <span class="count">(%s)</span>' . "\0" . 'Super administratoriai <span class="count">(%s)</span>','Super admins can no longer be added on the Options screen. You must now go to the list of existing users on Network Admin > Users and click on Username or the Edit action link below that name. This goes to an Edit User page where you can check a box to grant super admin privileges.'=>'Šiame puslapyje nuo šiol nebebus galima suteikti Tinklo administratoriaus privilegijų. Dabar tai galite padaryti redaguodami vartotojo paskyrą (Tinklo administravimas > Vartotojai).','Table ordered by Last Updated.'=>'Lentelė išrikiuota pagal paskiausią atnaujinimą.','Table ordered by Site Domain Name.'=>'Lentelė išrikiuota pagal tinklalapio domeną.','Table ordered by Site Path.'=>'Lentelė išrikiuota pagal tinklalapio vietą serveryje.','Table ordered by Site Registered Date.'=>'Lentelė išrikiuota pagal tinklalapio užregistravimo datą.','Table ordered by Theme Name.'=>'Lentelė išrikiuota pagal temos pavadinimą.','Table ordered by User Registered Date.'=>'Lentelė išrikiuota pagal vartotojų užsiregistravimo datą.','That&#8217;s all, stop editing! Happy publishing.'=>'Prašome neredaguoti žemiau šios eilutės. Sėkmės!','The Network creation panel is not for WordPress MU networks.'=>'Tinklo kūrimo įrankis nėra skirtas WordPress MU tinklams.','The Right Now widget on this screen provides current user and site counts on your network.'=>'Laukelyje „Trumpa informacija“ pateikiami tinkle registruotų vartotojų ir tinklalapių skaičiai.','The URL for the first comment on a new site.'=>'Pirmojo komentaro naujame tinklalapyje autoriaus URL.','The author of the first comment on a new site.'=>'Pirmojo komentaro autorius.','The bulk action will permanently delete selected users, or mark/unmark those selected as spam. Spam users will have posts removed and will be unable to sign up again with the same email addresses.'=>'Panaudoję funkciją „Masinis veiksmas“, galite negrįžtamai ištrinti keletą vartotojų arba pažymėti juos kaip brukalų platintojus. Brukalų platintojų įrašai bus pašalinti, tokiu el. pašto adresu nebebus leidžiama registruotis.','The choice of subdirectory sites is disabled if this setup is more than a month old because of permalink problems with &#8220;/blog/&#8221; from the main site. This disabling will be addressed in a future version.'=>'The choice of subdirectory sites is disabled if this setup is more than a month old because of permalink problems with &#8220;/blog/&#8221; from the main site. This disabling will be addressed in a future version.','The constant %s cannot be defined when creating a network.'=>'Konstanta %s negali būti apibrėžta kuriant tinklą.','The domain or path entered conflicts with an existing username.'=>'Įvestas domenas ar kelias konfliktuoja su egzistuojančiu vartotojo vardu.','The email address of the first comment author on a new site.'=>'Tai el.pašto adresas rodomas prie pavyzdinio komentaro autoriaus naujame tinklalapyje.','The first comment on a new site.'=>'Pirmasis komentaras naujame tinklalapyje.','The first page on a new site.'=>'Pirmasis naujo tinklalapio puslapis.','The first post on a new site.'=>'Pirmasis naujo tinklalapio įrašas.','The following words are reserved for use by WordPress functions and cannot be used as site names: %s'=>'Šie žodžiai yra rezervuoti WordPress funkcijoms ir negali būti panaudoti kaip tinklalapio pavadinimai: %s','The internet address of your network will be %s.'=>'Jūsų tinklo interneto adresas bus %s.','The main site in a sub-directory installation will need to use a modified permalink structure, potentially breaking existing links.'=>'Pagrindiniam tinklalapiui sub-direktorijoje reikės naudoti modifikuotą nuorodų struktūrą, kas gali sugadinti dabartines nuorodas.','The network could not be created.'=>'Klaida: tinklas negali būti sukurtas.','The next screen for Network Setup will give you individually-generated lines of code to add to your wp-config.php and .htaccess files. Make sure the settings of your FTP client make files starting with a dot visible, so that you can find .htaccess; you may have to create this file if it really is not there. Make backup copies of those two files.'=>'The next screen for Network Setup will give you individually-generated lines of code to add to your wp-config.php and .htaccess files. Make sure the settings of your FTP client make files starting with a dot visible, so that you can find .htaccess; you may have to create this file if it really is not there. Make backup copies of those two files.','The original configuration steps are shown here for reference.'=>'Originalūs konfigūravimo žingsniai rodomi čia kaip pavyzdys.','The requested action is not valid.'=>'Šis veiksmas negalimas.','The requested site does not exist.'=>'Toks tinklalapis neegzistuoja.','The username and a link to set the password will be mailed to this email address.'=>'Vartotojo vardas ir nuoroda slaptažodžio sukūrimui bus išsiųsta šiuo el. pašto adresu.','The welcome email sent to new site owners.'=>'Pasveikinimo laiškas, siunčiamas vartotojams, sukūrusiems naują tinklalapį.','The welcome email sent to new users.'=>'Pasisveikinimo laiškas, siunčiamas naujiems vartotojams.','Theme disabled.'=>'%s tema išjungta.','Theme enabled.'=>'Tema įjungta.','Themes can be enabled on a site by site basis by the network admin on the Edit Site screen (which has a Themes tab); get there via the Edit action link on the All Sites screen. Only network admins are able to install or edit themes.'=>'Tinklo administratorius gali įjungti atskiras temas konkrečiam tinklalapiui, jį redaguojant (Tinklalapiai > Visi tinklalapiai > Redaguoti > Temos). Tik tinklo administratoriai gali įdiegti ir redaguoti temas.','Themes list navigation'=>'Temų sąrašo navigacija','There is a pending change of the network admin email to %s.'=>'Yra laukiantis patvirtinimo tinklo administratoriaus el. pašto pakeitimas į %s.','There was an error creating the user.'=>'Kuriant vartotojo paskyrą, įvyko klaida.','These themes may be active on other sites in the network.'=>'Šios temos gali būti naudojamos kituose Jūsų tinklo tinklalapiuose.','These unique authentication keys are also missing from your %s file.'=>'Šių unikalių autentikacijos raktų taip pat trūksta jūsų %s faile.','This is the main table of all sites on this network. Switch between list and excerpt views by using the icons above the right side of the table.'=>'Čia matote pagrindinę visų tinkle esančių tinklalapių lentelę. Paspaudę ant dešiniajame viršutiniame kampe esančių piktogramų, galite pasirinkti, ar rodyti tik tinklalapių sąrašą, ar ir šiek tiek informacijos apie juos.','This screen allows you to configure a network as having subdomains (<code>site1.example.com</code>) or subdirectories (<code>example.com/site1</code>). Subdomains require wildcard subdomains to be enabled in Apache and DNS records, if your host allows it.'=>'This screen allows you to configure a network as having subdomains (<code>site1.example.com</code>) or subdirectories (<code>example.com/site1</code>). Subdomains require wildcard subdomains to be enabled in Apache and DNS records, if your host allows it.','This screen enables and disables the inclusion of themes available to choose in the Appearance menu for each site. It does not activate or deactivate which theme a site is currently using.'=>'Šiame puslapyje galite nustatyti, kurias temas galės naudoti tinkle esantys tinklalapiai. Tai nedaro jokios įtakos šiuo metu tinklalapiuose naudojamoms temoms.','This screen is for Super Admins to add new sites to the network. This is not affected by the registration settings.'=>'Tinklo administratoriai čia gali sukurti naują tinklo tinklalapį. Registracijos nustatymai tam įtakos neturi.','This screen sets and changes options for the network as a whole. The first site is the main site in the network and network options are pulled from that original site&#8217;s options.'=>'Šiame puslapyje nustatomi ir keičiami viso tinklo nustatymai. Pirmas tinklalapis yra pagrindinis tinklo tinklalapis.','This table shows all users across the network and the sites to which they are assigned.'=>'Šioje lentelėje rodomi visi tinklui priklausantys vartotojai ir jų tinklalapiai.','This theme may be active on other sites in the network.'=>'Ši tema gali būti naudojama kituose tinklo tinklalapiuose.','This unique authentication key is also missing from your %s file.'=>'Šio unikalaus autentikacijos rakto taip pat trūksta jūsų %s faile.','To add a new site, <strong>click Create a New Site</strong>.'=>'Norėdami pridėti naują tinklalapį, <strong>paspauskite „Kurti naują tinklalapį“</strong>','To add a new user, <strong>click Create a New User</strong>.'=>'Norėdami pridėti naują vartotoją, <strong>paspauskite „Kurti naują vartotoją“</strong>','To make your installation more secure, you should also add:'=>'Norėdami įdiegimą padaryti labiau apsaugotą taip pat turėtumėte pridėti:','To search for a site, <strong>enter the path or domain</strong>.'=>'Ieškodami tinklalapio, <strong>įveskite kelią ar domeną</strong>.','To search for a user or site, use the search boxes.'=>'Norėdami ieškoti vartotojo ar tinklalapio, pasinaudokite paieškos laukeliais.','To search for a user, <strong>enter an email address or username</strong>. Use a wildcard to search for a partial username, such as user&#42;.'=>'Ieškodami vartotojo, <strong>įveskite el. pašto adresą arba vartotojo vardą</strong>. Naudodami žvaigždutę, galite ieškoti pagal vartotojo vardo dalį, pvz., varden*','Unarchive'=>'Išarchyvuoti','Update your network'=>'Atnaujinti tinklą','Updates'=>'Atnaujinimai','Upgrade Network'=>'Atnaujinti tinklą','Upload Settings'=>'Įkėlimo nustatymai','Upload file types'=>'Leistinų įkelti failų tipai','Upload settings control the size of the uploaded files and the amount of available upload space for each site. You can change the default value for specific sites when you edit a particular site. Allowed file types are also listed (space separated only).'=>'Įkėlimo nustatymai riboja įkeliamų failų dydį ir serveryje suteikiamą vietą. Kiekvienam tinklalapiui pasirinktinai šiuos nustatymus galite pakeisti. Tarpeliais (ne kableliais!) atskirkite leidžiamų įkelti failų tipus.','User accounts may be registered'=>'Vartotojų registracija įjungta','User could not be added to this site.'=>'Nepavyko vartotojo pridėti į šią svetainę.','User created.'=>'Vartotojas sukurtas.','User is already a member of this site.'=>'Vartotojas jau yra šio tinklalapio narys.','Users are not allowed to register these sites. Separate names by spaces.'=>'Vartotojai negali registruoti tokių tinklalapių. Atskirkite pavadinimus tarpeliais.','Users deleted.'=>'Vartotojai ištrinti.','Users marked as spam.'=>'Vartotojai pažymėti kaip brukalų platintojai.','Users removed from spam.'=>'Vartotojai panaikinti iš brukalų platintojų sąrašo.','Users who are signed up to the network without a site are added as subscribers to the main or primary dashboard site, giving them profile pages to manage their accounts. These users will only see Dashboard and My Sites in the main navigation until a site is created for them.'=>'Tinklalapio neturintys vartotojai priskiriami pagrindiniam (arba kitam nustatymuose nurodytam) tinklalapiui, jiems suteikiama skaitytojo rolė, profilio puslapis ir galimybė tvarkyti savo paskyrą. Vėliau jie bet kada galės susikurti savo tinklalapį.','Visit Theme Site'=>'Aplankyti temos kūrėjo tinklalapį','Visit theme site for %s'=>'Aplankykite temos %s tinklalapį','Visit to go to the front-end of the live site.'=>'Spauskite čia jei norite gyvai pamatyti tinklalapį.','Warning! Problem updating %1$s. Your server may not be able to connect to sites running on it. Error message: %2$s'=>'Įspėjimas! Iškilo problemų atnaujinant %1$s. Jūsų serveris nepasiekiamas. Klaidos pranešimas: %2$s','Warning! User cannot be modified. The user %s is a network administrator.'=>'Dėmesio! Vartotojo paskyra negali būti modifikuojama. %s yra tinklo administratorius.','Welcome Email'=>'Informacinis laiškas I:<br/>Naujas tinklalapis','Welcome User Email'=>'Informacinis laiškas II:<br/>Naujas vartotojas','Welcome to the Network installation process!'=>'Sveiki atvykę į tinklo diegimo procesą!','Welcome to your Network Admin. This area of the Administration Screens is used for managing all aspects of your Multisite Network.'=>'Sveiki atvykę į tinklo administravimo skydelį. Čia galėsite valdyti savo tinklalapių tinklą.','What would you like to call your network?'=>'Kaip norėtumėte pavadinti savo tinklą?','WordPress has been updated! Next and final step is to individually upgrade the sites in your network.'=>'WordPress buvo atnaujintas! Dabar reikia individualiai atnaujinti tinkle esančius tinklalapius.','Y/m/d g:i:s a'=>'Y/m/d g:i:s a','Yes, delete these themes'=>'Taip, ištrinti šias temas','Yes, delete this theme'=>'Taip, ištrinti šią temą','You are about to activate the site %s.'=>'Jūs norite aktyvuoti tinklalapį %s.','You are about to archive the site %s.'=>'Jūs norite tinklalapį %s įtraukti į archyvą.','You are about to deactivate the site %s.'=>'Jūs norite išjungti tinklalapį %s.','You are about to delete the following sites:'=>'Ruošiatės ištrinti šias svetaines:','You are about to delete the site %s.'=>'Jūs ruošiatės ištrinti tinklalapį %s.','You are about to mark the site %s as mature.'=>'You are about to mark the site %s as mature.','You are about to mark the site %s as not mature.'=>'You are about to mark the site %s as not mature.','You are about to mark the site %s as spam.'=>'Jūs ruošiatės pažymėti tinklalapį %s kaip brukalą.','You are about to remove the following theme:'=>'Jūs norite ištrinti šią temą:','You are about to remove the following themes:'=>'Jūs norite pašalinti šias temas:','You are about to unarchive the site %s.'=>'Jūs norite išimti tinklalapį %s iš archyvo.','You are about to unspam the site %s.'=>'Jūs norite tinklalapį %s išimti iš brukalų sąrašo.','You can also go to the user&#8217;s profile page by clicking on the individual username.'=>'Jei paspausite ant vartotojo vardo, taip pat išvysite jo profilio redagavimo puslapį.','You can make an existing user an additional super admin by going to the Edit User profile page and checking the box to grant that privilege.'=>'Bet kuriam registruotam vartotojui galite priskirti tinklo administratoriaus teises. Eikite į vartotojo profilio redagavimo puslapį ir uždėkite varnelę ties atitinkamu nustatymu.','You can sort the table by clicking on any of the table headings and switch between list and excerpt views by using the icons above the users list.'=>'Lentelę galite surikiuoti paspaudę ant pirmosios eilutės elementų. Paspaudę ant piktogramų, esančių virš vartotojų sąrašo, galite pasirinkti, ar norite matyti tik sąrašą, ar ir ištraukas.','You cannot change this later.'=>'Vėliau to pakeisti negalėsite.','You cannot delete a theme while it is active on the main site.'=>'Jūs negalite ištrinti temos, kol ji yra naudojama pagrindiniame tinklalapyje.','You must define the %1$s constant as true in your %2$s file to allow creation of a Network.'=>'Privalote nustatyti %1$s konstantą į <code>true</code> %2$s faile, jei norite įjungti Tinklo sukūrimo galimybę.','You should back up your existing %1$s and %2$s files.'=>'Rekomenduojame sukurti %1$s ir %2$s failų kopijas.','You should back up your existing %s file.'=>'Išsaugokite atsarginę failo %s versiją.','You should consider changing your site domain to %1$s before enabling the network feature. It will still be possible to visit your site using the %3$s prefix with an address like %2$s but any links will not have the %3$s prefix.'=>'Rekomenduojame pakeisti tinklalapio domeną į %1$s prieš įjungiant tinklo funkciją. Vis dar galėsite pasiekti savo tinklalapį naudojant %3$s prefiksą su adresu, tokiu kaip %2$s, bet visos nuorodos neturės %3$s prefikso.','You will need a wildcard DNS record if you are going to use the virtual host (sub-domain) functionality.'=>'Jums reikės „wildcard“ DNS įrašo, jei norite naudoti sub-domenų pasirinkimą.','Your email address.'=>'Jūsų el. pašto adresas.','https://developer.wordpress.org/advanced-administration/server/web-server/nginx/'=>'https://developer.wordpress.org/advanced-administration/server/web-server/nginx/','siteActivate'=>'Įjungti','siteMark as spam'=>'Pažymėti kaip brukalą','siteNot Spam'=>'Ne brukalas','siteNot spam'=>'Ne brukalas','sitePublic'=>'Vieša','siteRegistered'=>'Registruota','siteSpam'=>'Brukalas','sitesAll <span class="count">(%s)</span>'=>'Visi <span class="count">(%s)</span>' . "\0" . 'Visi <span class="count">(%s)</span>' . "\0" . 'Visi <span class="count">(%s)</span>','sitesSpam <span class="count">(%s)</span>'=>'Šlamštas <span class="count">(%s)</span>' . "\0" . 'Šlamštas <span class="count">(%s)</span>' . "\0" . 'Šlamštas <span class="count">(%s)</span>','subdirectory exampleslike <code>%1$s/site1</code> and <code>%1$s/site2</code>'=>'kaip <code>%1$s/site1</code> ir <code>%1$s/site2</code>','subdomain exampleslike <code>site1.%1$s</code> and <code>site2.%1$s</code>'=>'kaip <code>site1.%1$s</code> ir <code>site2.%1$s</code>','theme%1$s by %2$s'=>'%1$s - %2$s','themesAll <span class="count">(%s)</span>'=>'Visos: <span class="count">(%s)</span>' . "\0" . 'Visos: <span class="count">(%s)</span>' . "\0" . 'Visos: <span class="count">(%s)</span>','themesBroken <span class="count">(%s)</span>'=>'Neveikianti <span class="count">(%s)</span>' . "\0" . 'Neveikiančios <span class="count">(%s)</span>' . "\0" . 'Neveikiančios <span class="count">(%s)</span>','themesDisabled <span class="count">(%s)</span>'=>'Išjungta <span class="count">(%s)</span>' . "\0" . 'Išjungtos <span class="count">(%s)</span>' . "\0" . 'Išjungtos <span class="count">(%s)</span>','themesEnabled <span class="count">(%s)</span>'=>'Įjungta <span class="count">(%s)</span>' . "\0" . 'Įjungtos <span class="count">(%s)</span>' . "\0" . 'Įjungtos <span class="count">(%s)</span>','themesUpdate Available <span class="count">(%s)</span>'=>'Galimi atnaujinimai: <span class="count">(%s)</span>' . "\0" . 'Galimi atnaujinimai: <span class="count">(%s)</span>' . "\0" . 'Galimi atnaujinimai: <span class="count">(%s)</span>','userMark as spam'=>'Pažymėti kaip brukalą','userNot spam'=>'Ne brukalas','userRegistered'=>'Registruotas','verb; siteArchive'=>'Archyvuoti']];
